function getScreenOrientation() {
  if (window.innerWidth > window.innerHeight) {
    Outputs.landscape();
  } else {
    if (window.innerWidth < 550) {
      Outputs.mobile();
    }else{
      Outputs.portrait();
    }

  }
}

function onWindowResize() {
  getScreenOrientation();
}

// Listen for window resize events
window.addEventListener('resize', onWindowResize);

// Call the function initially to get the current orientation and check the window width
onWindowResize();

// Initialize the canvas when the signal is sent
if (Inputs.InitCanvasSignal) {
  //let container = document.querySelector('.canvasContainer')
  let p5Image;
  let fetchInProgress = false;
  let imgSrcCheck;
  let startTime;
  let duration = 1800;
  let resolutions = [2, 4, 8, 16, 32, 64, 80, 100, 120];
  let currentIndex = 0;

  let canvas = document.createElement("div");
  document.querySelector('.canvasContainer').appendChild(canvas);

  sketch = function (p) {
    p.setup = function () {
      p.createCanvas(Inputs.width, Inputs.height)
      startTime = p.millis();
    }

    p.draw = function () {
      p.background(255)

      if (p5Image) {
        //p.image(p5Image, 0, 0, Inputs.width*2, Inputs.height*2)


        let elapsedTime = p.millis() - startTime;

        if (elapsedTime < duration) {
          let progress = elapsedTime / duration;
          let index = Math.floor(progress * resolutions.length);
          currentIndex = index;
        } else {
          currentIndex = -1;
        }
        if (currentIndex >= 0 && currentIndex < resolutions.length) {
          p.pixelateImage(p5Image, resolutions[currentIndex]);
        } else {
          p.image(p5Image, 0, 0);
        }
      }

      if(Inputs.pointerUp){
        fetchInProgress = true;
        p5Image = false;
      }

      if (fetchInProgress) {
        // true while on pointer up
        if(imgSrcCheck == Inputs.imgSrc){
          // already fetched return; 
          return;
        }else{
          // store src and continue
          imgSrcCheck = Inputs.imgSrc
        }
        var imgSrc = Inputs.imgSrc
        console.log(imgSrc)

        // fetch png img from src
        fetch(imgSrc)
          .then((response) => response.blob())
          .then((blob) => {
            let img = new Image()
            img.src = URL.createObjectURL(blob)
            img.onload = () => {
              p5Image = p.createImage(img.width, img.height)
              p5Image.drawingContext.drawImage(img, 0, 0)
              p5Image = p.cropRandomCorner(p5Image, p5Image.width/2, p5Image.height/2);
              fetchInProgress = false;
              startTime = p.millis();
            }
          })
          .catch((error) => {
            console.error('Error fetching image:', error)
          })

        fetchInProgress = false;
      }

    }
    p.pixelateImage = function (img, resolution) {
      // Calculate the size of each square based on the input resolution
      let squareWidth = img.width / resolution;
      let squareHeight = img.height / resolution;

      // Iterate through the image and sample the color at each step based on the input resolution
      for (let y = 0; y < img.height; y += squareHeight) {
        for (let x = 0; x < img.width; x += squareWidth) {
          let centerX = x + squareWidth / 2;
          let centerY = y + squareHeight / 2;

          // Get the color at the center of the square
          let color = img.get(centerX, centerY);
          // Set the fill color to the sampled color
          p.noStroke();
          p.fill(color);
          //p.ellipse(x, y, squareWidth+1, squareHeight+1);
          p.rect(x, y, squareWidth+1, squareHeight+1);
        }
      }
    }

    p.cropRandomCorner = function (img, cropWidth, cropHeight) {
      // Ensure the crop dimensions are smaller than the original image dimensions
      if (cropWidth > img.width || cropHeight > img.height) {
        console.error("Crop dimensions are larger than the original image dimensions.");
        return img;
      }

      // Randomly select one of the four corners
      let corner = Math.floor(p.random(4));

      let x, y;

      switch (corner) {
        case 0: // Top-left corner
          x = 0;
          y = 0;
          break;
        case 1: // Top-right corner
          x = img.width - cropWidth;
          y = 0;
          break;
        case 2: // Bottom-left corner
          x = 0;
          y = img.height - cropHeight;
          break;
        case 3: // Bottom-right corner
          x = img.width - cropWidth;
          y = img.height - cropHeight;
          break;
      }

      // Get the cropped section of the image
      let croppedImage = img.get(x, y, cropWidth, cropHeight);

      croppedImage.resize(Inputs.width, Inputs.height);
      return croppedImage;
    }

    p.windowResized = function () {
      p.resizeCanvas(Inputs.width, Inputs.height)
    }
  }

  p = new p5(sketch, canvas)
}

