let touchStartY;
let touchEndY;

function handleTouchStart(event) {
  // Get the height of the window
  const windowHeight = window.innerHeight;

  // Check if the touch event starts within the bottom 100px of the screen
  if (event.touches[0].clientY > windowHeight - 400) {
    touchStartY = event.touches[0].clientY;
  }
}

function handleTouchEnd(event) {
  if (touchStartY) {
    touchEndY = event.changedTouches[0].clientY;
    handleSwipe();
  }

  // Reset touchStartY to ensure the next swipe also starts within the bottom 100px
  touchStartY = null;
}

function handleSwipe() {
  if (touchEndY < touchStartY) {
    console.log("Swiped up");
    Outputs.SwipeUP();
    // Your code here to execute when a swipe up is detected
  } else if (touchEndY > touchStartY) {
    console.log("Swiped down");
    Outputs.SwipeDown();
    // Your code here to execute when a swipe down is detected
  }
}

// Add event listeners for touchstart and touchend events
document.addEventListener('touchstart', handleTouchStart, false);
document.addEventListener('touchend', handleTouchEnd, false);

